import React from 'react';

function AddTodoForm(props) {
    const [todoTitle, setTodoTitle] = React.useState('');

    function handleTitleChange(event) {
        let newTodoTitle = event.target.value;
        setTodoTitle(newTodoTitle);
    };

    function handleAddTodo(event) {
        event.preventDefault();
        document.getElementById("formValue").reset(); // Note to self: can also use event.target.reset() instead of document.getElementById()
        props.onAddTodo(todoTitle);                 
    };

    return (
        <form id="formValue" onSubmit={handleAddTodo}>
            <label>Title </label>
            <input id="todoTitle" type="text" name="title" value={todoTitle} onChange={handleTitleChange}></input>
            <label htmlFor="todoTitle"></label>
            <input type="submit" value="Add"/>
        </form>
    );
}

export { AddTodoForm }
